DROP TABLE IF EXISTS `diafan_shop`;
CREATE TABLE `diafan_shop` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'идентификатор',
  `name1` text NOT NULL COMMENT 'название',
  `act1` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'показывать на сайте: 0 - нет, 1 - да',
  `date_start` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'дата начала показа',
  `date_finish` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'дата окончания показа',
  `article` varchar(30) NOT NULL DEFAULT '' COMMENT 'артикул',
  `map_no_show` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'не показывать на карте сайта: 0 - нет, 1 - да',
  `changefreq` enum('always','hourly','daily','weekly','monthly','yearly','never') NOT NULL DEFAULT 'always' COMMENT 'Changefreq для sitemap.xml',
  `priority` varchar(3) NOT NULL DEFAULT '' COMMENT 'Priority для sitemap.xml',
  `cat_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'идентификатор основной категории из таблицы `diafan_shop_category`',
  `site_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'идентификатор страницы сайта из таблицы `diafan_site`',
  `brand_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'идентификатор производителя из таблицы `diafan_shop_brand`',
  `keywords1` varchar(250) NOT NULL DEFAULT '' COMMENT 'ключевые слова, тег Keywords',
  `descr1` text NOT NULL COMMENT 'описание, тэг Description',
  `canonical1` varchar(100) NOT NULL DEFAULT '' COMMENT 'канонический тег',
  `title_meta1` varchar(250) NOT NULL DEFAULT '' COMMENT 'заголовок окна в браузере, тег Title',
  `anons1` text NOT NULL COMMENT 'анонс',
  `anons_plus1` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'добавлять анонс к описанию: 0 - нет, 1 - да',
  `text1` text NOT NULL COMMENT 'описание',
  `yandex` text NOT NULL COMMENT 'данные для выгрузки в Яндекс.Маркет',
  `show_yandex` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'выгружать в Яндекс.Маркет: 0 - нет, 1 - да (если в настройках выбрана выгрузка только выбранных товаров)',
  `no_buy` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'товар временно отсутствует: 0 - нет, 1 - да',
  `import` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'товар только что импортирован: 0 - нет, 1 - да',
  `import_id` varchar(100) NOT NULL DEFAULT '' COMMENT 'собственный идентификатор товара при импорте',
  `sort` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'подрядковый номер для сортировки',
  `timeedit` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'время последнего изменения в формате UNIXTIME',
  `counter_buy` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'количество покупок',
  `hit` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'хит: 0 - нет, 1 - да',
  `new` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'новинка: 0 - нет, 1 - да',
  `action` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'акция: 0 - нет, 1 - да',
  `is_file` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'это товар-файл: 0 - нет, 1 - да',
  `access` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'доступ ограничен: 0 - нет, 1 - да',
  `admin_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'пользователь из таблицы `diafan_users`, добавивший или первый отредктировавший товар в административной части',
  `theme` varchar(50) NOT NULL DEFAULT '' COMMENT 'шаблон страницы сайта',
  `view` varchar(50) NOT NULL DEFAULT '' COMMENT 'шаблон модуля',
  `trash` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'запись удалена в корзину: 0 - нет, 1 - да',
  PRIMARY KEY (`id`),
  KEY `site_id` (`site_id`),
  KEY `brand_id` (`brand_id`)
) ENGINE=MyISAM AUTO_INCREMENT=659 DEFAULT CHARSET=utf8 COMMENT='Товары';

INSERT INTO `diafan_shop` VALUES ('1','Березовый веник','1','0','0','вн-001','0','monthly','0','3','17','2','','','','','','0','<p>Березовый веник &ndash; символ Русской бани, он самый распространенный и популярный тип банного веника. Белоствольная береза с ажурной асимметричной кроной &ndash; одно из самых любимых деревьев Россиян. Испокон веков она считалась священной, а в период язычества олицетворяла мать земных богатств.</p>\r\n<p>Березу ценят не только за красоту, но и за целебные свойства. Листья березы содержат большое количество фитонцидов, очищающих воздух, интенсивно дезинфицирующих его. Химический анализ показал, что в них есть дубильные вещества, эфирные масла, микроэлементы, витамин С и провитамин А.</p>\r\n<p>Берёзовый веник &ndash; прекрасный инструмент для массажа, поскольку в нем успешно соединяются гибкость и упругость ветвей с терпимой жесткостью и переносимой колкостью, а также поразительной пористостью березового листа, способного хорошо впитывать пот и вытягивать шлаки. Он успокаивающе действует на организм, очищает кожу, помогает при дерматите и экземе. Издревле его использовали для лечения артрита, почек, ревматизма и астмы.</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','1','1449250587','18','0','0','0','0','0','1','','','0'),('2','Дубовый веник','1','0','0','вн-002','0','monthly','0','3','17','2','','','','','','0','<p>Дуб считается священным деревом у многих народов. Возле него проводили обряды, у дерева просили здоровье и силу. Считалось, что те, кто живет у дуба, не стареют. Издавна дуб почитался за прочность древесины и за целебные свойства. Старинные Русские лечебники советовали лечить раны дубовым листом и мелкотолченой корой.</p>\r\n<p>В листьях дуба много полезных веществ &ndash; белков, пектинов, сахара, кислот, крахмала, но особенно ценятся они за дубильные свойства, которые представлены здесь в значительном количестве. Летучие вещества дуба имеют приятный аромат и благодаря вдыханию целебных запахов улучается работа легких, дыхание становиться чистым.</p>\r\n<p>Дубовый веник полезен людям с жирной кожей с соответствующими кожными заболеваниями. Под действием эфирных дубильных веществ кожа хорошо очищается, становиться упругой матовой. Благоухание распаренных дубовых листьев благотворно действует на нервную систему, проходят головные боли, отдышка, боли в сердце, раздражительность, улучшается настроение, нормализуется сон. Дубовый веник считается наиболее прочным и долговечным.&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','2','1449088004','22','1','0','0','0','0','1','','','0'),('3','Липовый веник','1','0','0','вн-003','0','monthly','0','3','17','2','','','','','','0','<p>В Европе Липа считалась священной, Овидий называл её &laquo;золотым деревом&raquo;. Древнее славяне посвятили липу Ладе &ndash; богине любви и красоты, срубить или сжечь липу считалось величайшим проступком. Тем не мене её часто использовали для вязки веников, причисляя к числу лучших.</p>\r\n<p>Липовый веник ценится за его удивительный аромат и целебные свойства, свойственные практически всем частям этого растения. В липе обнаружены эфирное масло, аскорбиновая кислота, каротин, сапонины, богатый набор минеральных солей и дубильные вещества.</p>\r\n<p>С помощью липового веника можно избавиться от простудных заболеваний и воспалительных процессов, банные процедуры устраняют головную боль, ускоряют потоотделение, оказывают успокаивающее и ранозаживляющее действие. Использование веника из веток липы в парной способствует выведению песка и камней из почек. &nbsp;&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','3','1449088179','6','0','0','0','0','0','1','','','0'),('4','Можжевеловый веник','1','0','0','вн-004','0','monthly','0','3','17','2','','','','','','0','<p>По древнерусским поверьям, ветви можжевельника помогают не только одолеть смертельный недуг, но и дают возможность обрести вечную жизнь. Несмотря на свою колючесть, можжевеловый веник, имеет немало почитателей.</p>\r\n<p>Можжевеловый веник &ndash; настоящий рекордсмен по уничтожению на коже болезнетворных микробов, поскольку хвоя этого растения оказывает мощное бактерицидное и дезинфицирующее действие. В состав всех частей растения входят эфирные масла, придающее ему неповторимый аромат, а также различный органические кислоты, смолы, витамины и дубильные вещества.</p>\r\n<p>Под действием паров, полный набор целебных веществ, входящих в состав растения, через кожу и легкие воздействует на весь организм. Можжевеловый веник помогает при заболеваниях верхних дыхательных путей, туберкулезе, отеках, патологии почек, печени, желчных путей и различных высыпания на коже.&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','4','1453987845','8','0','0','0','0','0','1','','','0'),('5','Пихтовый веник','1','0','0','вн-005','0','monthly','0','3','17','2','','','','','','0','<p>В сравнении с другими хвойными, пихта более мягкая и нежная. Пихтовые колючки не такие жесткие, как колючки ели или сосны и поэтому такими вениками могут пользоваться люди даже с чувствительной кожей. Настой из молоденьких игл пихты издавна употребляли в Сибири при усталости, простуде, цинге и ломоте в костях.</p>\r\n<p>Пихтовый веник хорошо применять в зимнюю пору. Ведь банные процедуры с использованием такого веника особенно эффективны при заболеваниях простудного и воспалительного характера. Пихта содержит в себе масло, полезное при многих заболеваниях. В бане полезные пихтовое масло, дубильные вещества и фитонциды проникают в поры тела и способствуют излечиванию кожных и ревматических заболеваний. После парной из тела уходит усталость, кожа очищается от болезненных микробов, исчезают кожные высыпания.</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','5','1450944409','13','0','0','1','0','0','1','','','0'),('6','Эвкалиптовый веник','1','0','0','вн-006','0','monthly','0','3','17','3','','','','','','0','<p>Эвкалипт &ndash; вечнозеленое дерево, которое в отличие от других древесных пород сбрасывает не листья, а кору. Эвкалиптовый веник часто применяют на Кавказе. Эвкалипт обладает многими полезными свойствами для нашего организма.</p>\r\n<p>Листья растения содержат 1-3% эфирного масла, которое обладает сильным антисептическим свойством. Основной составляющей эфирного масла эвкалипта, является цинеол, растворяющийся в холодной воде, даже лучше чем в горячей, что обязательно следует учитывать при запаривание веника.</p>\r\n<p>В парной эвкалиптовый веник благоухает удивительным ароматом, а источаемые растением эфирные масла глубоко проникают в легкие, очищая их от вредных веществ, после чего дыхание становиться легким и свободным. Чаще всего эвкалиптовый веник применяют в бане не столько для массажа сколько для ароматизации воздуха и облегчения дыхания.&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','6','1449056644','18','0','0','0','0','0','1','','','0'),('7','Серебристый эвкалипт','0','0','0','вн-007','0','monthly','0','3','17','4','','','','','','0','<p>Серебристый эвкалипт, обладает всеми целебными свойствами простого эвкалипта, при этом у него более концентрированный, насыщенный аромат. Он также богат эфирными маслами, а витаминов в нем, в два раза больше чем в обычном эвкалипте.</p>\r\n<p>Нам привозят эти веники из солнечной Абхазии. Ингаляции с эвкалиптом - распространенное средство в современной фитотерапии. В парной можно делать не только массаж, но и ингаляцию с эвкалиптовым веником.</p>\r\n<p>В листьях всех видов эвкалипта, применяемых в медицине, содержится эфирное масло, основным компонентом которого оказывается цинеол (ранее называвшийся эвкалиптолом). Химический состав также включает: пинен, терпены, дубильные вещества и фитонциды.</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','7','1449250535','0','0','0','0','0','0','1','','','0'),('8','Веник из Канадского дуба','1','0','0','вн-008','0','monthly','0','3','17','3','','','','','','0','<p>Канадский дуб также известен под названиями остролистый или красный дуб. Родина этого дерева Северная Америка, но много лет назад его завезли и в Россию. Теперь веники из этого замечательного дерева можно купить в Москве.</p>\r\n<p>Он совмещает в себе свойства кленового и дубового веника. Как и обычный дуб, богат дубильными веществами, эфирными маслами и витаминами. Кроме того, обладает всеми достоинствами клена, содержит аскорбиновую кислоту, хорошо поглощает пот, оттягивает на себя токсины и шлаки с кожи.</p>\r\n<p>Аромат канадского дуба сильнее, чем у простого дуба. Веник из канадского дуба показан людям с жирной кожей &ndash; он делает её матовой и упругой, оказывает противовоспалительное действие. Его еще называют &laquo;зимним&raquo; так как он очень долго не сбрасывает листву перед холодами. Это самый прочный банный веник, практически не осыпается его можно смело использовать несколько раз.</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','8','1449250592','8','0','0','0','0','0','1','','','0'),('9','Бамбуковый веник','0','0','0','вн-009','0','monthly','0','3','17','6','','','','','','0','<p>Бамбуковый веник - это массажер, из экологического материала. Представляет собой пучок связанных вместе тонких бамбуковых палочек. Китайские целители считают, что бамбук имеет положительную энергию и щедро делится этой энергией с теми, кто прикоснется к нему.</p>\r\n<p>Веник из бамбука замачивать не нужно, он продается готовым к использованию. Используют его в бане и сауне до или после распаривания кожи. Им снимают мышечное напряжение, выполняют точечный массаж тела. Считается, что парение с бамбуковым веником дарит не только отличный сон, но и улучшает самочувствие и настроение на долгое время.</p>\r\n<p>Массаж бамбуковым веником выравнивает и устраняет небольшие дефекты кожи, помогает избавиться от растяжек и целлюлита. Бамбуковый веник спасение для тех, кто страдает разными заболеваниями позвоночника &ndash; бамбук отлично вытягивает боль.&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','9','1449250527','0','0','1','0','0','0','1','','','0'),('10','Веник из Полыни ','1','0','0','тр-001','0','monthly','0','4','17','2','','','','','','0','<p>Полынь начали применять еще в древние времена, в трактатах по восточной медицине сохранилось упоминание полынной настойке. Само название травы, означает &laquo;здоровье&raquo;, потому что происходит оно от древнегреческого слова. Полынь отлично дезинфицирует воздух, во времена страшных эпидемий холеры, чумы её стелили на пол и окуривали дома.</p>\r\n<p>&nbsp;Славянские народы издавна вешали пучки этой травы в бане и добавляли в воду для ошпаривания каменки. Считалось, что полынь очищает пространство от злых духов. К плюсам полынного веника относят, и способность заметно улучшать память и умственные способности. Это пригодится тем, кто осваивает большой объём информации.&nbsp; или готовится к экзаменам.</p>\r\n<p>&nbsp;Настой полыни применяют при заболеваниях печени и почек, &nbsp;мигрени, туберкулезе, при лихорадке, отеках, &nbsp;изжоге. &nbsp;Это целебное растение в равной мере действенно и при желудочных заболеваниях, и при нарушениях функций желчного пузыря и чрезвычайно полезно после перенесенных желчных колик.</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','10','1449056632','8','1','0','0','0','0','1','','','0'),('11','Веник из Мяты','1','0','0','тр-002','0','monthly','0','4','17','2','','','','','Основные параметры товара','0','<p>Мята&nbsp;- одно из наиболее распространенных эфирных растений на планете. Существует множество видов мяты, но наиболее распространена мята английская перечная, её начали культивировать в Англии в конце 17 века, оттуда она и распространилась по всей Европе. &nbsp;Человек, вдохнувший аромат мяты, получает заряд бодрости и прекрасного настроения. В Древнем Риме, перед приемом знатных гостей сто&shy;лы в трапезной щедро натирались пучками мятной травы, а залы для тор&shy;жественных собраний обильно опрыскивались мятной водой.</p>\r\n<p>Чудесный, &nbsp;не обыкновенный в своем роде запах растения обусловлен на&shy;личием в нем большого количества эфирного масла сложного химического состава. Основным компонентом, которого является ментол, обладающий удивительной способностью суживать на поверхности тела кровеносные сосуды и одновременно рефлекторно расширять сосуды наших внутренних органов.На организм мята действует, возбуждающе, улучшает пищеварение, полезна при сердечных и легочных заболеваниях, успокаивает и нормализует работу желудка.<b></b></p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','11','1454300676','8','0','0','0','0','0','1','','','0'),('12','Веник из Донника','1','0','0','тр-003','0','monthly','0','4','17','2','','','','','','0','<p>Донник лекарственный - многолетнее травянистое растение из семейства Бобовых, в народе его &nbsp;еще называют: донник жёлтый или буркун. Растение имеет сильный кумариновым запах. Эта лекарственная трава считается медоносным растением. Донниковый мед является перворазрядным и обладает высокими вкусовыми качествами.</p>\r\n<p>Донник обладает исключительными целебными свойствами: его используют при истерии, &nbsp;головных болях и мигрени. Растение хорошо помогает при болезнях дыхательных органов. Кумарин, содержащийся в доннике, способствует повышению уровня артериального давления, улучшает кровообращения и мозговую деятельность. Обладает седативными свойствами, помогает при раздражительности, неврастении, меланхолии.</p>\r\n<p>В бане, донник отдает расслабляющий аромат - сладкий запах цветущих луговых трав. Его используют в качестве отваров, поливая каменку, добавляют его веточки в разные древесные веники. А можно просто повесить под потолком такой веничек и насладится запахом лета. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','12','1449056570','3','0','0','0','0','0','1','','','0'),('13','Веник из Багульника','1','0','0','тр-004','0','monthly','0','4','17','2','','','','','','0','<p>Багульник &ndash; необычайно красиво это растения во время цветения, его даже сравнивают с цветением Сакуры. Растет этот кустарник на болоте, обладает огромным количеством целительных свойств, которые находят применение в народной медицине с давних времен.</p>\r\n<p>В багульнике содержится большое количество эфирного масла с такими летучими веществами, как цимол, палюстрол, ледол. За счет обилия в нем этого эфирного масла багульник обладает довольно сильным запахом, напоминающим запах камфары; этот запах еще называют бальзамическим. Кроме того, в надземной части багульника болотного содержатся дубильные вещества, органические кислоты, &nbsp;смолы, &nbsp;витамины, гликозиды и минеральные вещества.</p>\r\n<p>Веточки багульника можно в &nbsp;небольшом количестве, развешивать на стенах парной бани или бросить на пол; эфирное масло, распространяясь в воздухе, окажет свое целительное действие.&nbsp;</p>','typePrefix=\nvendor=\nmodel=\nvendorCode=\nsales_notes=\nmanufacturer_warranty=\ncountry_of_origin=\nbid=\ncbid=','0','0','0','','13','1449056522','4','0','0','0','0','0','1','','','0'),('14','Фетровая шляпка &quot;Бабочка&quot;','1','0','0','фш-001','0','month