<?/* * Функция записывает в log.txt * переданные в $data параметры *  * @param array $data массив параметров * @return null  */function writeservicelog($data){        $file = 'logs/log.txt';    if(is_file($file)) if(filesize($file) > 3*1024)  unlink($file);        try{        $fh = fopen($file, "a+");        $log_data = $data['methodname'] .'||param||'.serialize($data['request']).'||param||'.serialize($data['response']).'||param||'.$data['time'].'||param||'.$data['srv_url'].'||param||'.date('Y-m-d H:i:s').'\n';        $s = fwrite($fh, $log_data);        fclose($fh);    } catch (Exception $e){        //    }    }?>