    <script type="text/javascript">
        $(function(){
            // find all the input elements with title attributes
            $('input[title!=""]').hint();
            $("#slider div.header a").dropShadow({left: 0, top: 0, blur: 2, color: "#000", opacity: 1.0});
            lastBlock = $("#slider ul li:first-child div.slide");
            maxWidth = 630;
            countSliders = $("#slider div.slide").length;
            minWidth = ($("#slider").width() - maxWidth - 2*(countSliders-1))/(countSliders-1);
            $("#slider div.slide").css("width", minWidth);
            $("#slider ul li:first-child div.slide").css("width", maxWidth);
            $("#slider div.header").css("left", (minWidth - 16) / 2);
            $("#slider ul li div.slide").hover(
                function(){
                    $(lastBlock).animate({width: minWidth+"px"}, { queue:false, duration:400 });
                    $(this).animate({width: maxWidth+"px"}, { queue:false, duration:400});
                    currentBlock = this;
                    lastBlock = this;
                }
            );
        });
    </script>
